$con = new PDO("mysql:host=".DB_HOST.";dbname=".DB_NAME, DB_USER, DB_PASS, array(PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES utf8"));

$sql = $con->prepare('SELECT * FROM `articulo` WHERE `id` = 1');
$sql->execute();
$res = $sql->fetch(PDO::FETCH_OBJ);
$articulo[0] = $res->titulo;
$articulo[1] = $res->contenido;


$sql = $con->prepare('SELECT `nombre`, `id` FROM `region`');
$sql->execute();
while($row = $sql->fetch(PDO::FETCH_OBJ)) {
	$allRegs[$row->id] = stripChars($row->nombre);
}
$sql = $con->prepare('SELECT `region` FROM `proy_regionales`');
$sql->execute();
while($row = $sql->fetch(PDO::FETCH_OBJ)) {
	if(!in_array(stripChars($row->region), $proyRegs)) {
		$clave = array_search(stripChars($row->region), $allRegs);
		$proyRegs[$clave] = stripChars($row->region);
	}
}
$count = count($proyRegs);

